                                                        },
                                                        body: JSON.stringify({
                                                            query: `
                query {
                  storeConfig {
                      listAlgoliaTrendingAttributeConfig {
                        listAttribute {
                          code
                          value
                          trending_label
                          shop_all_link
                          trending_id
                        }
                    }
                  }
                }
                    `,
                                                        }),
                                                    })
