                                                    .then((result) => {
                                                        const items =
                                                            result &&
                                                            result.data &&
                                                            result.data.storeConfig &&
                                                            result.data.storeConfig.listAlgoliaTrendingAttributeConfig &&
                                                            result.data.storeConfig.listAlgoliaTrendingAttributeConfig
                                                            .listAttribute;
                                                        const trendingTabs = document.getElementById("trending-tabs");
                                                        if (trendingTabs && items) {
                                                            let trendingId = items[0].trending_id;
                                                            const trendingBlock = document.querySelector(
                                                                `.algolia-id-${trendingId}`
                                                            );
                                                            if (trendingBlock) {
                                                                trendingBlock.classList.add("active");
                                                            }
                                                            items.forEach((element) => {
                                                                trendingTabs.insertAdjacentHTML(
                                                                    "beforeend",
                                                                    `<span class="btn-new-design tag tab-item ${trendingId === element.trending_id ? 'active' : ''}" data-trending-id="${element.trending_id}">
                      ${element.trending_label}
                  </span>
               `
                                                                );
                                                            });
                                                        }
                                                    })
                                                    .catch((error) => {
                                                        console.log("error", error);
                                                    });

                                                function waitForAddedNode(params) {
                                                    return new Promise((resolve, reject) => {
                                                        new MutationObserver(function(mutations) {
                                                            const el = document.querySelector(params.classSelector);

                                                            if (el) {
                                                                this.disconnect();
                                                                resolve(el);
                                                            }
                                                            // update the params object
                                                        }).observe(params.parent || document, {
                                                            subtree: !!params.recursive,
                                                            childList: true,
                                                        });
                                                    });
                                                }
                                                waitForAddedNode({
                                                        classSelector: "#trending-tabs .tab-item",
                                                        recursive: true,
                                                    })
                                                    .then(function() {
                                                        var tabs = document.querySelectorAll("#trending-tabs .tab-item");
                                                        tabs.forEach(function(tab) {
